import React, { useState, useEffect } from 'react';
import styles from '../../assets/css/styles';
import Toast from 'react-native-toast-message';

import { Picker } from '@react-native-picker/picker';
import { View, Text, FlatList, Modal, TextInput, TouchableOpacity } from 'react-native';
import { fetchProducto, agregarNuevoProducto, fetchCategoria } from '../../assets/js/Inventario/Producto.js';
import { FontAwesome } from "@expo/vector-icons";


export default function Producto() {

    const [modalVisible, setModalVisible] = useState(false);
    const [codigo, setCodigo] = useState('');
    const [nombre, setNombre] = useState('');
    const [categoriaId, setCategoriaId] = useState('');
    const [estado, setEstado] = useState('');


    const [productos, setProducto] = useState([]);

    useEffect(() => {
        fetchProductoData();
    }, []);

    const fetchProductoData = async () => {
        try {
            const productosData = await fetchProducto();
            setProducto(productosData);
        } catch (error) {
            console.error('Error al obtener las categorías:', error);
        }
    };


    const CategoriaPicker = () => {
        // Crear un estado para guardar las categorias
        const [categorias, setCategorias] = useState([]);
        // Crear un estado para guardar el valor seleccionado
        const [categoriaId, setCategoriaId] = useState('0');

        // Usar useEffect para obtener las categorias al montar el componente
        useEffect(() => {
            // Llamar a la función fetchCategoria y guardar el resultado en el estado
            fetchCategoria().then(data => setCategorias(data));
        }, []);
    }

    const handleGuardar = async () => {
        const nuevoProducto = { Codigo: codigo, Nombre: nombre, Estado: estado };

        try {
            const successMessage = await agregarNuevoProducto(nuevoProducto);
            if (successMessage) {
                Toast.show({
                    type: 'success',
                    text1: 'Producto creado',
                    text2: successMessage,
                });

                fetchProductoData();
                closeModal();
            } else {
                console.error('Error al agregar producto');
            }
        } catch (error) {
            console.error('Error en la solicitud POST:', error);
        }
    };

    const renderHeader = () => (
        <View>
            <View style={styles.header}>
                <Text style={styles.headerText}>Id</Text>
                <Text style={styles.headerText}>Código</Text>
                <Text style={styles.headerText}>Descripción</Text>
                <Text style={styles.headerText}>Estado</Text>
            </View>
        </View>
    );

    const renderItem = ({ item }) => (
        <View style={styles.Items}>
            <Text style={styles.itemText}>{item.id}</Text>
            <Text style={styles.itemText}>{item.Codigo}</Text>
            <Text style={styles.itemText}>{item.Nombre}</Text>
            <Text style={[styles.itemText, { color: item.Estado === 'Activo' ? 'green' : 'red' }]}> {item.Estado} </Text>
        </View>

    );

    const renderFooter = () => (
        <View style={styles.footer}>
            <Text style={styles.footerText}>Total: {productos.length} productos</Text>
        </View>
    );

    const openModal = () => {
        setModalVisible(true);
    };

    const closeModal = () => {
        setModalVisible(false);
    };

    return (
        <View style={styles.container}>

            <Text style={styles.title}>Productos</Text>

            <TouchableOpacity style={styles.addButton} onPress={openModal} >
                <FontAwesome name="plus" style={styles.Icon} />
            </TouchableOpacity>

            <Modal animationType="slide" visible={modalVisible} onRequestClose={closeModal} >
                <View style={styles.modalContainer}>
                    <Text style={styles.modalTitle}>Agregar nueo producto</Text>
                    <View style={styles.modalFieldContainer}>
                        <Text style={styles.modalLabel}>Código<Text style={styles.requiredAsterisk}>*</Text> :</Text>
                        <View style={styles.modalInputContainer}>
                            <TextInput style={styles.modalInput} placeholder="Ingrese el código" onChangeText={text => setCodigo(text)} value={codigo} />
                        </View>
                    </View>
                    <View style={styles.modalFieldContainer}>
                        <Text style={styles.modalLabel}>Nombre<Text style={styles.requiredAsterisk}>*</Text> :</Text>
                        <View style={styles.modalInputContainer}>
                            <TextInput style={styles.modalInput} placeholder="Ingrese la descripción" onChangeText={text => setNombre(text)} value={nombre} />
                        </View>
                    </View>
                    <View style={styles.modalFieldContainer}>
                        <Text style={styles.modalLabel}>Categoria<Text style={styles.requiredAsterisk}>*</Text>:</Text>
                        <View style={styles.pickerContainer}>
                            <Picker selectedValue={categoriaId} style={styles.picker} onValueChange={(itemValue) => { console.log("Valor seleccionado:", itemValue); if (itemValue !== '0') { setCategoriaId(itemValue); } }} >
                                <Picker.Item label="-- Seleccione Categoria --" value="0" />

                            </Picker>
                        </View>
                    </View>
                    <View style={styles.modalFieldContainer}>
                        <Text style={styles.modalLabel}>Estado<Text style={styles.requiredAsterisk}>*</Text>:</Text>
                        <View style={styles.pickerContainer}>
                            <Picker selectedValue={estado} style={styles.picker} onValueChange={(itemValue) => { console.log("Valor seleccionado:", itemValue); if (itemValue !== '0') { setEstado(itemValue); } }} >
                                <Picker.Item label="-- Seleccione Estado --" value="0" />
                                <Picker.Item label="Activo" value="Activo" />
                                <Picker.Item label="Inactivo" value="Inactivo" />
                            </Picker>

                        </View>
                    </View>
                    <View style={styles.modalButtonsContainer}>
                        <TouchableOpacity style={[styles.modalButton, styles.saveButton]} onPress={handleGuardar} >
                            <Text style={styles.modalButtonText}>Guardar</Text>
                        </TouchableOpacity>
                        <TouchableOpacity style={[styles.modalButton, styles.cancelButton]} onPress={closeModal} >
                            <Text style={styles.modalButtonText}>Cancelar</Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </Modal>
            <FlatList
                data={productos}
                keyExtractor={(item) => item.id.toString()}
                renderItem={renderItem}
                ListHeaderComponent={renderHeader}
                ListFooterComponent={renderFooter}
                contentContainerStyle={styles.List} />
        </View>
    );
}
