import React, { useState, useEffect } from 'react';
import { View, Text, FlatList } from 'react-native';
import { fetchCategorias } from '../../assets/js/Inventario/Categoria';
import styles from '../../assets/css/styles';

export default function Categorias() {

    const [categorias, setCategorias] = useState([]);

    useEffect(() => {
        fetchCategorias()
            .then(data => {
                setCategorias(data);
            })
            .catch(error => {
                console.error('Error al obtener las categorías:', error);
            });
    }, []);

    const renderHeader = () => (
        <View style={styles.header}>
            <Text style={styles.headerText}>Código</Text>
            <Text style={styles.headerText}>Descripción</Text>
            <Text style={styles.headerText}>Estado</Text>
        </View>
    );

    const renderItem = ({ item }) => (
        <View style={styles.categoryItem}>
            <Text style={styles.categoryText}>{item.Codigo}</Text>
            <Text style={styles.categoryText}>{item.Descripcion}</Text>
            <Text style={styles.categoryText}>{item.Estado}</Text>
        </View>
    );

    const renderFooter = () => (
        <View style={styles.footer}>
            <Text style={styles.footerText}>Total: {categorias.length} categorías</Text>
        </View>
    );

    return (
        <View style={{ flex: 1 }}>
            <Text style={styles.title}>Categorías</Text>
            <FlatList
                data={categorias}
                keyExtractor={(item) => item.id.toString()}
                renderItem={renderItem}
                ListHeaderComponent={renderHeader}
                ListFooterComponent={renderFooter}
                contentContainerStyle={styles.categoryList}
            />
        </View>
    );
}
