import React from 'react';
import { StyleSheet } from 'react-native';
import { Text, View, Image } from 'react-native';
import {  MaterialIcons } from "@expo/vector-icons";
import { NavigationContainer } from "@react-navigation/native";
import { createDrawerNavigator, DrawerItemList, DrawerContentScrollView } from "@react-navigation/drawer";

import User from "./assets/user.jpg";
import Categoria from "./screens/Inventario/Categoria";
import Producto from "./screens/Inventario/Producto";
import Inventario from "./screens/Inventario/Inventario";
import InventarioHabitacion from "./screens/Inventario/InventarioHabitacion"

const Drawer = createDrawerNavigator();

const options = [
    { label: "Categorias", component: Categoria },
    { label: "Productos", component: Producto },
    { label: "Inventarios", component: Inventario },
    { label: "Inventario Habitacion", component: InventarioHabitacion },
  ];

const CustomDrawerContent = (props) => {
    return (
        <DrawerContentScrollView >
            <View style={styles.profileContainer}>
                <Image source={User} style={styles.profileImage} />
                <Text style={styles.profileName}>Juliana Martinez</Text>
                <Text style={styles.profileRole}>Administrador</Text>
            </View>
            <DrawerItemList {...props} />
        </DrawerContentScrollView>
    );
};

export default function App() {

    const [selectedOption, setSelectedOption] = useState(options[0].component);


    return (
        <NavigationContainer>
            <Drawer.Navigator drawerContent={CustomDrawerContent} screenOptions={{ drawerStyle: { backgroundColor: "#fff", width: 250 }, headerStyle: { backgroundColor: "#28a745", }, headerTintColor: "#fff", headerTitleStyle: { fontWeight: "bold" }, drawerActiveTintColor: "blue", drawerLabelStyle: { color: "#111" } }}>
                <Drawer.Screen
                    name="Categorias"
                    component={Categoria}
                    options={{
                        drawerLabel: "Categorias",
                        title: "Categorias",
                        drawerIcon: ({ color, size }) => (
                            <MaterialIcons name="category" size={size} color={color} />
                        )
                    }}
                />
                <Drawer.Screen
                    name="Productos"
                    component={Producto}
                    options={{
                        drawerLabel: "Productos",
                        title: "Productos",
                        drawerIcon: ({ color, size }) => (
                            <MaterialIcons name="shopping-cart" size={size} color={color} />
                        )
                    }}
                />
                <Drawer.Screen
                    name="Inventarios"
                    component={Inventario}
                    options={{
                        drawerLabel: "Inventarios",
                        title: "Inventarios",
                        drawerIcon: ({ color, size }) => (
                            <MaterialIcons name="inventory" size={size} color={color} />
                        )
                    }}
                />
                <Drawer.Screen
                    name="Inventario Habitacion"
                    component={InventarioHabitacion}
                    options={{
                        drawerLabel: "Inventario Habitacion",
                        title: "Inventario Habitacion",
                        drawerIcon: ({ color, size }) => (
                            <MaterialIcons name="single-bed" size={size} color={color} />
                        )
                    }}
                />
            </Drawer.Navigator>
        </NavigationContainer>
    );

}

const styles = StyleSheet.create({
    profileContainer: {
        height: 200,
        width: '100%',
        justifyContent: 'center',
        alignItems: 'center',
        borderBottomColor: "#f4f4f4",
        borderBottomWidth: 1,
    },
    profileImage: {
        height: 130,
        width: 130,
        borderRadius: 65,
    },
    profileName: {
        fontSize: 22,
        marginVertical: 6,
        fontWeight: "bold",
        color: "#111",
    },
    profileRole: {
        fontSize: 16,
        color: "#111",
    },
});
