import React, { useState } from 'react';
import { Text, View, TouchableOpacity } from 'react-native';
import { MaterialIcons } from "@expo/vector-icons";
import Categoria from "./screens/Inventario/Categoria";
import Producto from "./screens/Inventario/Producto";
import Inventario from "./screens/Inventario/Inventario";
import InventarioHabitacion from "./screens/Inventario/InventarioHabitacion";

export default function App() {
    const [selectedOption, setSelectedOption] = useState(null);

    const options = [
        { label: "Categorias", component: Categoria },
        { label: "Productos", component: Producto },
        { label: "Inventarios", component: Inventario },
        { label: "Inventario Habitacion", component: InventarioHabitacion },
    ];

    return (
        <View>
            <View style={{ flexDirection: 'row', alignItems: 'center' }}>
                <Text>Selecciona una opci√≥n:</Text>
                <TouchableOpacity onPress={() => setSelectedOption(null)}>
                    <Text style={{ color: 'blue', marginLeft: 5 }}>Cerrar</Text>
                </TouchableOpacity>
            </View>

            {selectedOption ? (
                React.createElement(selectedOption.component)
            ) : (
                options.map((option, index) => (
                    <TouchableOpacity
                        key={index}
                        onPress={() => setSelectedOption(option)}
                        style={{ flexDirection: 'row', alignItems: 'center' }}
                    >
                        <MaterialIcons name={option.iconName} size={24} color="black" />
                        <Text style={{ marginLeft: 5 }}>{option.label}</Text>
                    </TouchableOpacity>
                ))
            )}
        </View>
    );
}
