import React, { useState } from 'react';
import { Text, View, TouchableOpacity } from 'react-native';
import { MaterialIcons } from "@expo/vector-icons";
import { NavigationContainer } from "@react-navigation/native";
import { createDrawerNavigator } from "@react-navigation/drawer";
import Categoria from "./screens/Inventario/Categoria";
import Producto from "./screens/Inventario/Producto";
import Inventario from "./screens/Inventario/Inventario";
import InventarioHabitacion from "./screens/Inventario/InventarioHabitacion";
import { StyleSheet } from 'react-native';
import CustomDrawerContent from './CustomDrawerContent';



const Drawer = createDrawerNavigator();

export default function App() {
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);

    return (
        <NavigationContainer>
            <Drawer.Navigator drawerContent={CustomDrawerContent}>
                <Drawer.Screen
                    name="Dropdown"
                    options={{ drawerLabel: "Dropdown" }} >
                    {() => (
                        <View>
                            <TouchableOpacity
                                onPress={() => setIsDropdownOpen(!isDropdownOpen)}
                                style={{
                                    flexDirection: "row",
                                    alignItems: "center"
                                }} >
                                <MaterialIcons
                                    name={isDropdownOpen ? "expand-less" : "expand-more"}
                                    size={24}
                                    color="black"
                                />
                                <Text>Dropdown</Text>
                            </TouchableOpacity>

                            {isDropdownOpen && (
                                <View style={{ marginLeft: 20 }}>
                                    <Drawer.Screen
                                        name="Categorias"
                                        component={Categoria}
                                        options={{
                                            drawerLabel: "Categorias",
                                            title: "Categorias",
                                            drawerIcon: ({ color, size }) => (
                                                <MaterialIcons name="category" size={size} color={color} />
                                            )
                                        }}
                                    />
                                    <Drawer.Screen
                                        name="Productos"
                                        component={Producto}
                                        options={{
                                            drawerLabel: "Productos",
                                            title: "Productos",
                                            drawerIcon: ({ color, size }) => (
                                                <MaterialIcons name="shopping-cart" size={size} color={color} />
                                            )
                                        }}
                                    />
                                    <Drawer.Screen
                                        name="Inventarios"
                                        component={Inventario}
                                        options={{
                                            drawerLabel: "Inventarios",
                                            title: "Inventarios",
                                            drawerIcon: ({ color, size }) => (
                                                <MaterialIcons name="inventory" size={size} color={color} />
                                            )
                                        }}
                                    />
                                    <Drawer.Screen
                                        name="Inventario Habitacion"
                                        component={InventarioHabitacion}
                                        options={{
                                            drawerLabel: "Inventario Habitacion",
                                            title: "Inventario Habitacion",
                                            drawerIcon: ({ color, size }) => (
                                                <MaterialIcons name="single-bed" size={size} color={color} />
                                            )
                                        }}
                                    />
                                </View>
                            )}
                        </View>
                    )}
                </Drawer.Screen>
            </Drawer.Navigator>
        </NavigationContainer>
    );
}

const styles = StyleSheet.create({
    profileContainer: {
        height: 200,
        width: '100%',
        justifyContent: 'center',
        alignItems: 'center',
        borderBottomColor: "#f4f4f4",
        borderBottomWidth: 1,
    },
    profileImage: {
        height: 130,
        width: 130,
        borderRadius: 65,
    },
    profileName: {
        fontSize: 22,
        marginVertical: 6,
        fontWeight: "bold",
        color: "#111",
    },
    profileRole: {
        fontSize: 16,
        color: "#111",
    },
});
