import { StyleSheet, Platform } from 'react-native';

export default StyleSheet.create({
    title: {
        fontSize: 24,
        fontWeight: 'bold',
        textAlign: 'center',
        marginVertical: 20,
    },
    categoryList: {
        paddingHorizontal: 10, // Ajusta el padding horizontal para mejor responsive
        paddingBottom: Platform.OS === 'web' ? 50 : 0, // Espacio adicional en web
    },
    header: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        backgroundColor: '#f4f4f4',
        paddingVertical: 10,
        paddingHorizontal: 5,
        borderTopWidth: 1,
        borderBottomWidth: 1,
        borderColor: '#ccc',
    },
    headerText: {
        fontSize: 16,
        fontWeight: 'bold',
    },
    categoryItem: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        paddingVertical: 10,
        borderTopWidth: 1,
        borderBottomWidth: 1,
        borderColor: '#ccc',
    },
    categoryText: {
        fontSize: 16,
    },
    footer: {
        flexDirection: 'row',
        justifyContent: 'flex-end',
        paddingVertical: 10,
        borderTopWidth: 1,
        borderColor: '#ccc',
        marginTop: 20, // Espacio adicional en la parte inferior
    },
    footerText: {
        fontSize: 16,
        fontWeight: 'bold',
    },
});
