import { StyleSheet, Dimensions } from 'react-native';

const windowHeight = Dimensions.get('window').height;

export default StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'white',
        shadowColor: '#000',
        shadowOffset: {
            width: 0,
            height: 2
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 5
    },
    title: {
        fontSize: 24,
        fontWeight: 'bold',
        textAlign: 'center',
        marginVertical: 20
    },
    categoryList: {
        paddingHorizontal: 20
    },
    header: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        backgroundColor: '#f4f4f4',
        paddingVertical: 10,
        paddingHorizontal: 5,
        borderTopWidth: 1,
        borderBottomWidth: 1,
        borderColor: '#ccc'
    },
    headerText: {
        fontSize: 16,
        fontWeight: 'bold'
    },
    categoryItem: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        paddingVertical: 10,
        borderTopWidth: 1,
        borderBottomWidth: 1,
        borderColor: '#ccc'
    },
    categoryText: {
        fontSize: 16,
        textAlign: 'center'
    },
    footer: {
        flexDirection: 'row',
        justifyContent: 'flex-end',
        paddingVertical: 10,
        borderTopWidth: 1,
        borderColor: '#ccc'
    },
    footerText: {
        fontSize: 16,
        fontWeight: 'bold'
    },
    addButton: {
        alignSelf: 'flex-start',
        marginTop: 5,
        marginLeft: 20,
        backgroundColor: '#2196f3',
        paddingVertical: 6,
        marginVertical: 20,
        paddingHorizontal: 12,
        borderRadius: 100,
        alignItems: 'center'
    },
    Icon: {
        color: 'white',
        fontSize: 25,
        fontWeight: 'bold',
        margin: 'auto'
    },
    modalContainer: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'white',
        padding: 50,
    },
    modalTitle: {
        fontSize: 20,
        fontWeight: 'bold',
        marginBottom: 20,
    },
    modalInput: {
        width: '100%',
        marginBottom: 15,
        paddingVertical: 10,
        paddingHorizontal: 15,
        borderWidth: 1,
        borderColor: '#ccc',
        borderRadius: 5,
    },
    modalButtonsContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        marginTop: 20,
        width: '100%',
    },
    modalButton: {
        paddingVertical: 10,
        paddingHorizontal: 20,
        borderRadius: 20,
        alignItems: 'center',
        justifyContent: 'center',
    },
    saveButton: {
        backgroundColor: 'green',
    },
    cancelButton: {
        backgroundColor: 'gray',
    },
    modalButtonText: {
        color: 'white',
        fontWeight: 'bold',
    },
});
