import React, { useState, useEffect } from 'react';
import { Picker } from '@react-native-picker/picker';
import { View, Text, FlatList, Modal, TextInput, TouchableOpacity } from 'react-native';
import { fetchCategorias } from '../../assets/js/Inventario/Categoria';
import styles from '../../assets/css/styles';
import { FontAwesome } from "@expo/vector-icons";


export default function Categorias() {

    const [modalVisible, setModalVisible] = useState(false);
    const [codigo, setCodigo] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [estado, setEstado] = useState('');

    const [categorias, setCategorias] = useState([]);

    // const [selectedAction, setSelectedAction] = useState('editar');

    useEffect(() => {
        fetchCategoriasData();
    }, []);

    const fetchCategoriasData = async () => {
        try {
            const data = await fetchCategorias();
            setCategorias(data);
        } catch (error) {
            console.error('Error al obtener las categorías:', error);
        }
    };

    const handleGuardar = async () => {
        const nuevaCategoria = { Codigo: codigo, Descripcion: descripcion, Estado: estado };

        try {
            const response = await axios.post('https://hotel-api-hzf6.onrender.com/api/inventario/categoria', nuevaCategoria);
            if (response.status === 201) {
                fetchCategoriasData(); // Actualizar la lista
                closeModal(); // Cerrar la modal
            } else {
                console.error('Error al agregar categoría');
            }
        } catch (error) {
            console.error('Error en la solicitud POST:', error);
        }
    };


    const renderHeader = () => (
        <View>
            <View style={styles.header}>
                <Text style={styles.headerText}>Código</Text>
                <Text style={styles.headerText}>Descripción</Text>
                <Text style={styles.headerText}>Estado</Text>
                {/* <Text style={styles.headerText}>Acciones</Text> */}
            </View>
        </View>
    );

    const renderItem = ({ item }) => (
        <View style={styles.categoryItem}>
            <Text style={styles.categoryText}>{item.Codigo}</Text>
            <Text style={styles.categoryText}>{item.Descripcion}</Text>
            <Text style={[styles.categoryText, { color: item.Estado === 'Activo' ? 'green' : 'red' }]}> {item.Estado} </Text>
            {/* <Picker selectedValue={selectedAction} style={{ height: 50, width: 150 }} onValueChange={(itemValue, itemIndex) => setSelectedAction({ value: itemValue, id: categorias[itemIndex].id })} >
                <Picker.Item label="Editar" value="editar" />
                <Picker.Item label="Eliminar" value="eliminar" />
            </Picker> */}
        </View>

    );

    const renderFooter = () => (
        <View style={styles.footer}>
            <Text style={styles.footerText}>Total: {categorias.length} categorías</Text>
        </View>
    );

    const openModal = () => {
        setModalVisible(true);
    };

    const closeModal = () => {
        setModalVisible(false);
    };

    return (
        <View style={styles.container}>

            <Text style={styles.title}>Categorías</Text>

            <TouchableOpacity style={styles.addButton} onPress={openModal} >
                <FontAwesome name="plus" style={styles.Icon} />
            </TouchableOpacity>

            <Modal animationType="slide" visible={modalVisible} onRequestClose={closeModal} >
                <View style={styles.modalContainer}>
                    <Text style={styles.modalTitle}>Agregar nueva categoría</Text>
                    <View style={styles.modalFieldContainer}>
                        <Text style={styles.modalLabel}>Código<Text style={styles.requiredAsterisk}>*</Text> :</Text>
                        <View style={styles.modalInputContainer}>
                            <TextInput style={styles.modalInput} placeholder="Ingrese el código" onChangeText={text => setCodigo(text)} value={codigo}
                            />
                        </View>
                    </View>
                    <View style={styles.modalFieldContainer}>
                        <Text style={styles.modalLabel}>Descripción<Text style={styles.requiredAsterisk}>*</Text> :</Text>
                        <View style={styles.modalInputContainer}>
                            <TextInput style={styles.modalInput} placeholder="Ingrese la descripción" onChangeText={text => setDescripcion(text)} value={descripcion} />
                        </View>
                    </View>
                    <View style={styles.modalFieldContainer}>
                        <Text style={styles.modalLabel}>Estado<Text style={styles.requiredAsterisk}>*</Text>:</Text>
                        <View style={styles.pickerContainer}>
                            <Picker selectedValue={estado} style={styles.picker} onValueChange={(itemValue) => { if (itemValue !== '0') { setEstado(itemValue); } }}>
                                <Picker.Item label="Activo" value="Activo" />
                                <Picker.Item label="Inactivo" value="Inactivo" />
                            </Picker>
                        </View>

                    </View>
                    <View style={styles.modalButtonsContainer}>
                        <TouchableOpacity style={[styles.modalButton, styles.saveButton]} onPress={handleGuardar} >
                            <Text style={styles.modalButtonText}>Guardar</Text>
                        </TouchableOpacity>
                        <TouchableOpacity style={[styles.modalButton, styles.cancelButton]} onPress={closeModal} >
                            <Text style={styles.modalButtonText}>Cancelar</Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </Modal>
            <FlatList
                data={categorias}
                keyExtractor={(item) => item.id.toString()}
                renderItem={renderItem}
                ListHeaderComponent={renderHeader}
                ListFooterComponent={renderFooter}
                contentContainerStyle={styles.categoryList} />
        </View>
    );
}
